import './App.css';
import React from 'react';
const ListItem = ()=> {
   const rows =[];
  for(let i = 0; i < 10; i++){
  rows.push(  <div className="Context-Item">
    <div style={{ width: '75%', height:'15%' }}>Free Code Camp </div>
    <button style={{ borderWidth: 0, borderColor: 'black', borderStyle: 'solid', width: '25%' }}>Delete</button>
  </div>)
}
return (rows);
}
function App() {
  return (
    <div className="App">
      <h1>Todo App</h1>
      <div className="Inputs">
        <input type="text" className="Text-input" placeholder="Add a New Todo" />
        <button type="button" className="Add-Button">Add To List</button>
      </div>
      <div className="Context-List">
      {
       <ListItem/> 
      }

      </div>
    </div>
  );
}

export default App;
