import './App.css';
import React,{useState} from 'react';

const ListItem = ({InputBox})=> {
   const rows =[];
   console.log("Todu",InputBox)
  for(let i = 0; i < 10; i++){
  rows.push(  <div className="Context-Item">
    <div style={{ width: '75%', height:'15%' }}>Free Code Camp </div>
    <div style={{ borderWidth: 0, borderColor: 'black', borderStyle: 'solid', width: '25%',cursor:"pointer" }}>Delete</div>
  </div>)
}
return (rows);
}
function App() {
  const [TodoList,SetTodoList] =useState([]);
  const [InputBox,SetInputBox] =useState();
  return (
    <div className="App">
      <h1>Todo App</h1>
      <div className="Inputs">
        <input type="text" className="Text-input"
         placeholder="Add a New Todo"
         value ={InputBox}
          onChange ={(e)=> {SetInputBox(e.target.value)
                            console.log({InputBox})}} />
        <button type="button" className="Add-Button" onClick={()=>SetTodoList(...TodoList, InputBox)}>Add To List</button>
      </div>
      <div className="Context-List">
      {
       <ListItem props={TodoList} />

      }

      </div>
    </div>
  );
}

export default App;
