import './App.css';
import React,{useState} from 'react';

const AddToList = (SetTodoList,TodoList,InputBox) => {
  if(InputBox.length > 0){
    let Task = InputBox.trim();
  if(Task.length > 0){
     SetTodoList([...TodoList, InputBox])
  }
  }


}
const DeleteInList = (ItemIndex,List,SetTodoList) => {

  const newList = List.filter((index)=> index !== ItemIndex);
  SetTodoList(newList);
}

const ListItem = (props)=> {
  const {List,SetTodoList} = props;
   const rows =[];
   console.log("Todu",List)
  for(let i = 0; i < List.length; i++){
  rows.push(  <div className="Context-Item">
    <div style={{ width: '75%', height:'15%' }}>{List[i]} </div>
    <div style={{ borderWidth: 0, borderColor: 'black', borderStyle: 'solid', width: '25%',cursor:"pointer" }} onClick={(e)=> DeleteInList(List[i],List,SetTodoList) } >Delete</div>
  </div>)
}
return (rows);
}
function App() {
  const [TodoList,SetTodoList] =useState([]);
  const [InputBox,SetInputBox] =useState();
  return (
    <div className="App">
      <h1>Todo App</h1>
      <div className="Inputs">
        <input type="text" className="Text-input"
         placeholder="Add a New Todo"
         value ={InputBox}
          onChange ={(e)=> {SetInputBox(e.target.value)
                            console.log({TodoList})}} />
        <button type="button" className="Add-Button" onClick={()=> AddToList(SetTodoList,TodoList,InputBox)}>Add To List</button>
      </div>
      <div className="Context-List">
      {
       <ListItem List={TodoList} SetTodoList={SetTodoList} />

      }

      </div>
    </div>
  );
}

export default App;
