import './App.css';
import React, { useState,useRef} from 'react';

const AddToList = (SetTodoList, TodoList, InputBox, SetInputBox) => {
  if (!InputBox.length > 0) {
    alert("Bos Ekleme Yapılamaz")
  }
  let Task = InputBox.trim();
  if (Task.length > 0) {
    SetTodoList([...TodoList, InputBox])
    SetInputBox("");
  }

}
const DeleteInList = (ItemIndex, List, SetTodoList) => {
  const newList = List.filter((index) => index !== ItemIndex);
  SetTodoList(newList);
}

const handleDelete=()=>{
const newStyle = contextItem.current;
newStyle.classList.toggle('is-Delete');
} 


const ListItem = (props) => {
  const contextItem = useRef(null);
  const { List, SetTodoList } = props;
  window.localStorage.setItem("List", JSON.stringify(List));
  const rows = [];
  for (let i = 0; i < List.length; i++) {
    rows.push(<div className="Context-Item" key={i} useRef={contextItem}>
      <div className="Context-Item-Text">{List[i]} </div>
      <div className="Context-Item-DeleteBtn" onClick={(e) => {DeleteInList(List[i], List, SetTodoList); handleDelete()}} >Delete</div>
    </div>)
  }
  return (rows.reverse());
}


function App() {
  const [TodoList, SetTodoList] = useState(window.localStorage.getItem("List") ? JSON.parse(window.localStorage.getItem('List')) : []);
  const [InputBox, SetInputBox] = useState("");
  return (
    <div className="App">
      <h1>Todo App</h1>
      <div className="Inputs">
        <input type="text" className="Text-input"
          placeholder="Add a New Todo"
          value={InputBox}
          onKeyDown={(e) => {
            if (e.key === "Enter") {
              AddToList(SetTodoList, TodoList, InputBox, SetInputBox);
            } else if (e.key === "Delete") {
              DeleteInList(TodoList[TodoList.length - 1], TodoList, SetTodoList)
            }
          }}
          onChange={(e) => { SetInputBox(e.target.value) }} />
        <button type="button" className="Add-Button" onClick={() => AddToList(SetTodoList, TodoList, InputBox, SetInputBox)}>Add To List</button>
      </div>
      <div className="Context-List">
        {
          <ListItem List={TodoList} SetTodoList={SetTodoList} />

        }

      </div>
    </div>
  );
}

export default App;
