import React, { Component } from 'react'
const PageSize = window.innerWidth;
import {MdAdd,MdDelete } from 'react-icons/md';
export default class Inputs extends Component {
    state = {
        InputBox:"",
    }
    render() {
        const {TodoList,SetTodoList} = this.props;  
        return (
            <div className="Inputs">
            <input type="text" className="Text-input"
              placeholder="Add a New Todo"
              value={InputBox}
              onKeyDown={(e) => {
                if (e.key === "Enter") {
                  AddToList(SetTodoList, TodoList, InputBox, SetInputBox);
                } else if (e.key === "Delete") {
                  DeleteInList(TodoList[TodoList.length - 1], TodoList, SetTodoList)
                }
              }}
              onChange={(e) => { SetInputBox(e.target.value) }} />
            <button type="button" className="Add-Button" onClick={() => AddToList(SetTodoList, TodoList, InputBox, SetInputBox)}><MdAdd size="24" /> {PageSize > 500 ? " Add To List":null}</button>
          </div>
        )
    }
}
